//  LoginPage - Formulario de Login
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from './AuthContext';

export const LoginPage = () => {
  const navigate = useNavigate();
  const { login, loading, error } = useAuth();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [validationError, setValidationError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
    setValidationError('');
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  setValidationError('');

  // Validaci贸n b谩sica
  if (!formData.username || !formData.password) {
    setValidationError('Por favor completa todos los campos');
    return;
  }

  // Validaci贸n de longitud m铆nima
  if (formData.username.length < 3) {
    setValidationError('El usuario/email debe tener al menos 3 caracteres');
    return;
  }

  if (formData.password.length < 1) {
    setValidationError('La contrase帽a es obligatoria');
    return;
  }

  try {
    await login(formData.username, formData.password);
    navigate('/dashboard');
  } catch (err) {
    setValidationError(err.message || 'Error al iniciar sesi贸n');
  }
};

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-500 to-blue-700">
      <div className="bg-white rounded-lg shadow-2xl p-8 w-full max-w-md">
        {/* Encabezado */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800">Gesti贸n Pastoral</h1>
          <p className="text-gray-600 mt-2">Inicia sesi贸n en tu cuenta</p>
        </div>

        {/* Errores */}
        {(validationError || error) && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-6">
            {validationError || error}
          </div>
        )}

        {/* Formulario */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-700 font-semibold mb-2">
              Correo Electr贸nico
            </label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="tu@email.com"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled={loading}
            />
          </div>

          <div>
            <label className="block text-gray-700 font-semibold mb-2">
              Contrase帽a
            </label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="⑩⑩⑩⑩⑩⑩⑩"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled={loading}
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white font-semibold py-2 rounded-lg hover:bg-blue-700 transition disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Iniciando sesi贸n...' : 'Iniciar Sesi贸n'}
          </button>
        </form>

        {/* Footer */}
        <div className="mt-6 text-center">
          <p className="text-gray-600">
            驴No tienes cuenta?{' '}
            <Link to="/register" className="text-blue-600 hover:underline font-semibold">
              Reg铆strate aqu铆
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};
